mapper.select:
select b.in_out_id,b.in_out_code,b.in_out_time,b.type,b.creator,b.num,b.money,c.user_name,c.audit_time,c.remark
,case when b.state = 1 then '已完成' when c.is_pass is not null and c.is_pass = 1 then '已通过'  when c.is_pass is not null and c.is_pass = 0 then '未通过' else '未审核' end state
from (
  select t.* from (
  select a.in_out_id,a.in_out_code,a.in_out_time,a.type,a.creator,a.num,a.money,a.state
  from stock_in_out a
  where a.clinic_id = #{clinic} and (a.type='进货' or a.type='退货')
  and a.order_id = #{code}
  and date_format(a.in_out_time,'%Y-%m-%d') = #{time}
  and a.type = #{type}
  order by a.state,a.in_out_time desc) t LIMIT ${limit} OFFSET ${page}
) b left join stock_audit c on b.in_out_id = c.in_out_id order by b.in_out_time desc,b.state

select b.in_out_code,b.in_out_time,b.type,b.creator,b.num,b.money,c.user_name,c.audit_time,c.remark
,case when b.state = 1 then '已完成' when c.is_pass is not null and c.is_pass = 1 then '已通过'  when c.is_pass is not null and c.is_pass = 0 then '未通过' else '未审核' end state
from (
select a.in_out_id,a.in_out_code,a.in_out_time,a.type,a.creator,a.num,a.money,a.state
from stock_in_out a
where a.clinic_id = #{clinic} and (a.type='进货' or a.type='退货')
and a.order_id = #{code}
and date_format(a.in_out_time,'%Y-%m-%d') = #{time}
and a.type = #{type}
order by a.state,a.in_out_time desc
) b left join stock_audit c on b.in_out_id = c.in_out_id

select b.in_out_id,b.in_out_code,b.in_out_time,b.type,b.creator_id,b.creator,b.num,b.money,b.clinic_id,b.org_name,c.user_name,c.audit_time,c.remark
,case when b.state = 1 then '已完成' when b.state = 0.5 then '已发送' when c.is_pass is not null and c.is_pass = 1 then '已通过'  when c.is_pass is not null and c.is_pass = 0 then '未通过' else '未审核' end state
from (
select t.* from ( select a.in_out_id,a.in_out_code,a.in_out_time,a.type,a.creator_id,a.creator,a.num,a.money,a.state,a.clinic_id,b.org_name
from stock_in_out a,sec_org b where a.clinic_id != #{clinic} and(a.type='进货' or a.type='退货') and a.clinic_id = b.id_
and a.in_out_code = #{code}
and date_format(a.in_out_time,'%Y-%m-%d') = #{time}
and a.type = #{type}
order by a.state,a.in_out_time desc) t LIMIT ${limit} OFFSET ${page}
) b left join stock_audit c on b.in_out_id = c.in_out_id order by b.in_out_time desc,b.state

select count(*) from ( select a.in_out_id
from stock_in_out a where a.clinic_id != #{clinic} and (a.type='进货' or a.type='退货')
and a.in_out_code = #{code}
and date_format(a.in_out_time,'%Y-%m-%d') = #{time}
and a.type = #{type}) t

